package Assignements;

public class Assgin_7_GlobalVariable 
{
static int a=20;
static int b=10;
static void add()
{
	System.out.println(a+b);
}
static void sub()
{
	System.out.println(a-b);
}
static void mul()
{
	System.out.println(a*b);
}
static void devide()
{
	System.out.println(a/b);
}


public static void main(String[] args) {
	// TODO Auto-generated method stub
	add();
	sub();
	mul();
	devide();
	

}
}